/*global describe, it*/
"use strict";

var fs = require("fs"),
	es = require("event-stream"),
	should = require("should");

require("mocha");

delete require.cache[require.resolve("../")];

var gutil = require("gulp-util"),
	cssImageDimensions = require("../");

describe("gulp-css-image-dimensions", function () {

	var expectedFile = new gutil.File({
		path: "test/expected/test.css",
		cwd: "test/",
		base: "test/expected",
		contents: fs.readFileSync("test/expected/test.css")
	});

	it("should produce expected file via buffer (using singlequotes)", function (done) {

		var srcFile = new gutil.File({
			path: "test/fixtures/test-singlequotes.css",
			cwd: "test/",
			base: "test/fixtures",
			contents: fs.readFileSync("test/fixtures/test-singlequotes.css")
		});

		var stream = cssImageDimensions();

		stream.on("error", function(err) {
			should.exist(err);
			done(err);
		});

		stream.on("data", function (newFile) {

			should.exist(newFile);
			should.exist(newFile.contents);

			String(newFile.contents).should.equal(String(expectedFile.contents));
			done();
		});

		stream.write(srcFile);
		stream.end();
	});

	it("should produce expected file via buffer (using doublequotes)", function (done) {

		var srcFile = new gutil.File({
			path: "test/fixtures/test-doublequotes.css",
			cwd: "test/",
			base: "test/fixtures",
			contents: fs.readFileSync("test/fixtures/test-doublequotes.css")
		});

		var stream = cssImageDimensions();

		stream.on("error", function(err) {
			should.exist(err);
			done(err);
		});

		stream.on("data", function (newFile) {

			should.exist(newFile);
			should.exist(newFile.contents);

			String(newFile.contents).should.equal(String(expectedFile.contents));
			done();
		});

		stream.write(srcFile);
		stream.end();
	});

	it("should allow using combined statements on one line", function (done) {

		var srcFile = new gutil.File({
			path: "test/fixtures/test-chained-usage.css",
			cwd: "test/",
			base: "test/fixtures",
			contents: fs.readFileSync("test/fixtures/test-chained-usage.css")
		});

		var expectedFile = new gutil.File({
			path: "test/expected/chained-usage.css",
			cwd: "test/",
			base: "test/expected",
			contents: fs.readFileSync("test/expected/chained-usage.css")
		});

		var stream = cssImageDimensions();

		stream.on("error", function(err) {
			should.exist(err);
			done(err);
		});

		stream.on("data", function (newFile) {

			should.exist(newFile);
			should.exist(newFile.contents);

			String(newFile.contents).should.equal(String(expectedFile.contents));
			done();
		});

		stream.write(srcFile);
		stream.end();
	});

	it("should error on stream", function (done) {

		var srcFile = new gutil.File({
			path: "test/fixtures/test-doublequotes.css",
			cwd: "test/",
			base: "test/fixtures",
			contents: fs.createReadStream("test/fixtures/test-doublequotes.css")
		});

		var stream = cssImageDimensions();

		stream.on("error", function(err) {
			should.exist(err);
			done();
		});

		stream.on("data", function (newFile) {
			newFile.contents.pipe(es.wait(function(err, data) {
				done(err);
			}));
		});

		stream.write(srcFile);
		stream.end();
	});

});
